ery.LambdaQueryWrapper;\\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\\nimport jakarta.servlet.http.HttpServletResponse;\\nimport org.springframework.stereotype.Service;\\nimport org.springframework.transaction.annotation.Transactional;\\nimport org.springframework.web.multipart.MultipartFile;\\nimport vip.xiaonuo.common.enums.CommonSortOrderEnum;\\nimport vip.xiaonuo.common.exception.CommonException;\\nimport vip.xiaonuo.common.page.CommonPageRequest;\\nimport java.math.BigDecimal;\\nimport java.util.Date;\\nimport vip.xiaonuo.ewm.modular.project.entity.EwmProject;\\nimport vip.xiaonuo.ewm.modular.project.mapper.EwmProjectMapper;\\nimport vip.xiaonuo.ewm.modular.project.param.EwmProjectAddParam;\\nimport vip.xiaonuo.ewm.modular.project.param.EwmProjectEditParam;\\nimport vip.xiaonuo.ewm.modular.project.param.EwmProjectIdParam;\\nimport vip.xiaonuo.ewm.modular.project.param.EwmProjectPageParam;\\nimport vip.xiaonuo.ewm.modular.project.service.EwmProjectService;\\n\\nimport vip.xiaonuo.common.util.CommonDownloadUtil;\\nimport vip.xiaonuo.common.util.CommonResponseUtil;\\n\\nimport java.io.File;\\nimport java.io.IOException;\\nimport java.util.List;\\n\\n/**\\n * 项目表Service接口实现类\\n *\\n * @author Richai\\n * @date  2025/12/30 14:28\\n **/\\n@Service\\npublic class EwmProjectServiceImpl extends ServiceImpl<EwmProjectMapper, EwmProject> implements EwmProjectService {\\n\\n    @Override\\n    public Page<EwmProject> page(EwmProjectPageParam ewmProjectPageParam) {\\n        QueryWrapper<EwmProject> queryWrapper = new QueryWrapper<EwmProject>().checkSqlInjection();\\n        if(ObjectUtil.isNotEmpty(ewmProjectPageParam.getProjectName())) {\\n            queryWrapper.lambda().like(EwmProject::getProjectName, ewmProjectPageParam.getProjectName());\\n        }\\n        if(ObjectUtil.isNotEmpty(ewmProjectPageParam.getProjectLeader())) {\\n            queryWrapper.lambda().like(EwmProject::getProjectLeader, ewmProjectPageParam.getProjectLeader());\\n        }\\n        if(ObjectUtil.isAllNotEmpty(ewmProjectPageParam.getSortField(), ewmProjectPageParam.getSortOrder())) {\\n            CommonSortOrderEnum.validate(ewmProjectPageParam.getSortOrder());\\n            queryWrapper.orderBy(true, ewmProjectPageParam.getSortOrder().equals(CommonSortOrderEnum.ASC.getValue()),\\n                    StrUtil.toUnderlineCase(ewmProjectPageParam.getSortField()));\\n        } else {\\n            queryWrapper.lambda().orderByAsc(EwmProject::getSortCode);\\n        }\\n        return this.page(CommonPageRequest.defaultPage(), queryWrapper);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void add(EwmProjectAddParam ewmProjectAddParam) {\\n        EwmProject ewmProject = BeanUtil.toBean(ewmProjectAddParam, EwmProject.class);\\n        this.save(ewmProject);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void edit(EwmProjectEditParam ewmProjectEditParam) {\\n        EwmProject ewmProject = this.queryEntity(ewmProjectEditParam.getId());\\n        BeanUtil.copyProperties(ewmProjectEditParam, ewmProject);\\n        this.updateById(ewmProject);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void delete(List<EwmProjectIdParam> ewmProjectIdParamList) {\\n        // 执行删除\\n        this.removeByIds(CollStreamUtil.toList(ewmProjectIdParamList, EwmProjectIdParam::getId));\\n    }\\n\\n    @Override\\n    public EwmProject detail(EwmProjectIdParam ewmProjectIdParam) {\\n        return this.queryEntity(ewmProjectIdParam.getId());\\n    }\\n\\n    @Override\\n    public EwmProject queryEntity(String id) {\\n        EwmProject ewmProject = this.getById(id);\\n        if(ObjectUtil.isEmpty(ewmProject)) {\\n            throw new CommonException(\\\"项目表不存在，id值为：{}\\\", id);\\n        }\\n        return ewmProject;\\n    }\\n\\n    @Override\\n    public void downloadImportTemplate(HttpServletResponse response) throws IOException {\\n       File tempFile = null;\\n       try {\\n         List<EwmProjectEditParam> dataList = CollectionUtil.newArrayList();\\n         String fileName = \\\"项目表导入模板_\\\" + DateUtil.format(DateTime.now(), DatePattern.PURE_DATETIME_PATTERN) + \\\".xlsx\\\";\\n         tempFile = FileUtil.file(FileUtil.getTmpDir() + FileUtil.FILE_SEPARATOR + fileName);\\n         EasyExcel.write(tempFile.getPath(), EwmProjectEditParam.class).sheet(\\\"项目表\\\").doWrite(dataList);\\n         CommonDownloadUtil.download(tempFile, response);\\n       } catch (Exception e) {\\n         log.error(\\\">>> 项目表导入模板下载失败：\\\", e);\\n         CommonResponseUtil.renderError(response, \\\"项目表导入模板下载失败\\\");\\n       } finally {\\n         FileUtil.del(tempFile);\\n       }\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public JSONObject importData(MultipartFile file) {\\n        try {\\n            int successCount = 0;\\n            int errorCount = 0;\\n            JSONArray errorDetail = JSONUtil.createArray();\\n            // 创建临时文件\\n            File tempFile = FileUtil.writeBytes(file.getBytes(), FileUtil.file(FileUtil.getTmpDir() +\\n                    FileUtil.FILE_SEPARATOR + \\\"ewmProjectImportTemplate.xlsx\\\"));\\n            // 读取excel\\n            List<EwmProjectEditParam> ewmProjectEditParamList =  EasyExcel.read(tempFile).head(EwmProjectEditParam.class).sheet()\\n                    .headRowNumber(1).doReadSync();\\n            List<EwmProject> allDataList = this.list();\\n            for (int i = 0; i < ewmProjectEditParamList.size(); i++) {\\n                JSONObject jsonObject = this.doImport(allDataList, ewmProjectEditParamList.get(i), i);\\n                if(jsonObject.getBool(\\\"success\\\")) {\\n                    successCount += 1;\\n                } else {\\n                    errorCount += 1;\\n                    errorDetail.add(jsonObject);\\n                }\\n            }\\n            return JSONUtil.createObj()\\n                    .set(\\\"totalCount\\\", ewmProjectEditParamList.size())\\n                    .set(\\\"successCount\\\", successCount)\\n                    .set(\\\"errorCount\\\", errorCount)\\n                    .set(\\\"errorDetail\\\", errorDetail);\\n        } catch (Exception e) {\\n            log.error(\\\">>> 项目表导入失败：\\\", e);\\n            throw new CommonException(\\\"项目表导入失败\\\");\\n        }\\n    }\\n\\n    public JSONObject doImport(List<EwmProject> allDataList, EwmProjectEditParam ewmProjectEditParam, int i) {\\n        String id = ewmProjectEditParam.getId();\\n        String projectName = ewmProjectEditParam.getProjectName();\\n        String projectLeader = ewmProjectEditParam.getProjectLeader();\\n        String orgId = ewmProjectEditParam.getOrgId();\\n        String peojectStatus = ewmProjectEditParam.getPeojectStatus();\\n        if(ObjectUtil.hasEmpty(id, projectName, projectLeader, orgId, peojectStatus)) {\\n            return JSONUtil.createObj().set(\\\"index\\\", i + 1).set(\\\"success\\\", false).set(\\\"msg\\\", \\\"必填字段存在空值\\\");\\n        } else {\\n            try {\\n                int index = CollStreamUtil.toList(allDataList, EwmProject::getId).indexOf(ewmProjectEditParam.getId());\\n                EwmProject ewmProject;\\n                boolean isAdd = false;\\n                if(index == -1) {\\n                    isAdd = true;\\n                    ewmProject = new EwmProject();\\n                } else {\\n                    ewmProject = allDataList.get(index);\\n                }\\n                BeanUtil.copyProperties(ewmProjectEditParam, ewmProject);\\n                if(isAdd) {\\n                    allDataList.add(ewmProject);\\n                } else {\\n                    allDataList.remove(index);\\n                    allDataList.add(index, ewmProject);\\n                }\\n                this.saveOrUpdate(ewmProject);\\n                return JSONUtil.createObj().set(\\\"success\\\", true);\\n            } catch (Exception e) {\\n              log.error(\\\">>> 数据导入异常：\\\", e);\\n              return JSONUtil.createObj().set(\\\"success\\\", false).set(\\\"index\\\", i + 1).set(\\\"msg\\\", \\\"数据导入异常\\\");\\n            }\\n        }\\n    }\\n\\n    @Override\\n    public void exportData(List<EwmProjectIdParam> ewmProjectIdParamList, HttpServletResponse response) throws IOException {\\n       File tempFile = null;\\n       try {\\n         List<EwmProjectEditParam> dataList;\\n         if(ObjectUtil.isNotEmpty(ewmProjectIdParamList)) {\\n            List<String> idList = CollStreamUtil.toList(ewmProjectIdParamList, EwmProjectIdParam::getId);\\n            dataList = BeanUtil.copyToList(this.listByIds(idList), EwmProjectEditParam.class);\\n         } else {\\n            dataList = BeanUtil.copyToList(this.list(), EwmProjectEditParam.class);\\n         }\\n         String fileName = \\\"项目表_\\\" + DateUtil.format(DateTime.now(), DatePattern.PURE_DATETIME_PATTERN) + \\\".xlsx\\\";\\n         tempFile = FileUtil.file(FileUtil.getTmpDir() + FileUtil.FILE_SEPARATOR + fileName);\\n         EasyExcel.write(tempFile.getPath(), EwmProjectEditParam.class).sheet(\\\"项目表\\\").doWrite(dataList);\\n         CommonDownloadUtil.download(tempFile, response);\\n       } catch (Exception e) {\\n         log.error(\\\">>> 项目表导出失败：\\\", e);\\n         CommonResponseUtil.renderError(response, \\\"项目表导出失败\\\");\\n       } finally {\\n         FileUtil.del(tempFile);\\n       }\\n    }\\n}\\n\"}]},\"traceId\":\"c77300d91ceb465a85a3e5f1a7545d8f\"}', '2025-12-30 14:28:36', '超管', '0618dc4d011986817e7729538c0d726619339f1f2cca0d9a0420e13bfc82e5aa6c8ff656a49d350caa207be41d64aaccf3551584b49c46e8fcf89e09e4d543b2', '2025-12-30 14:28:36', '-1', NULL, NULL);
