.multipart.MultipartFile;\\nimport vip.xiaonuo.common.enums.CommonSortOrderEnum;\\nimport vip.xiaonuo.common.exception.CommonException;\\nimport vip.xiaonuo.common.page.CommonPageRequest;\\nimport java.math.BigDecimal;\\nimport java.util.Date;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.entity.SysFiles;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.mapper.SysFilesMapper;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.param.SysFilesAddParam;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.param.SysFilesEditParam;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.param.SysFilesIdParam;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.param.SysFilesPageParam;\\nimport vip.xiaonuo.filemanger.modular.sysfiles.service.SysFilesService;\\n\\nimport vip.xiaonuo.common.util.CommonDownloadUtil;\\nimport vip.xiaonuo.common.util.CommonResponseUtil;\\n\\nimport java.io.File;\\nimport java.io.IOException;\\nimport java.util.List;\\n\\n/**\\n * 文件管理表Service接口实现类\\n *\\n * @author Richai\\n * @date  2026/01/30 17:35\\n **/\\n@Service\\npublic class SysFilesServiceImpl extends ServiceImpl<SysFilesMapper, SysFiles> implements SysFilesService {\\n\\n    @Override\\n    public Page<SysFiles> page(SysFilesPageParam sysFilesPageParam) {\\n        QueryWrapper<SysFiles> queryWrapper = new QueryWrapper<SysFiles>().checkSqlInjection();\\n        if(ObjectUtil.isNotEmpty(sysFilesPageParam.getName())) {\\n            queryWrapper.lambda().like(SysFiles::getName, sysFilesPageParam.getName());\\n        }\\n        if(ObjectUtil.isNotEmpty(sysFilesPageParam.getContentType())) {\\n            queryWrapper.lambda().eq(SysFiles::getContentType, sysFilesPageParam.getContentType());\\n        }\\n        if(ObjectUtil.isAllNotEmpty(sysFilesPageParam.getSortField(), sysFilesPageParam.getSortOrder())) {\\n            CommonSortOrderEnum.validate(sysFilesPageParam.getSortOrder());\\n            queryWrapper.orderBy(true, sysFilesPageParam.getSortOrder().equals(CommonSortOrderEnum.ASC.getValue()),\\n                    StrUtil.toUnderlineCase(sysFilesPageParam.getSortField()));\\n        } else {\\n            queryWrapper.lambda().orderByAsc(SysFiles::getSortCode);\\n        }\\n        return this.page(CommonPageRequest.defaultPage(), queryWrapper);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void add(SysFilesAddParam sysFilesAddParam) {\\n        SysFiles sysFiles = BeanUtil.toBean(sysFilesAddParam, SysFiles.class);\\n        this.save(sysFiles);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void edit(SysFilesEditParam sysFilesEditParam) {\\n        SysFiles sysFiles = this.queryEntity(sysFilesEditParam.getId());\\n        BeanUtil.copyProperties(sysFilesEditParam, sysFiles);\\n        this.updateById(sysFiles);\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public void delete(List<SysFilesIdParam> sysFilesIdParamList) {\\n        // 执行删除\\n        this.removeByIds(CollStreamUtil.toList(sysFilesIdParamList, SysFilesIdParam::getId));\\n    }\\n\\n    @Override\\n    public SysFiles detail(SysFilesIdParam sysFilesIdParam) {\\n        return this.queryEntity(sysFilesIdParam.getId());\\n    }\\n\\n    @Override\\n    public SysFiles queryEntity(String id) {\\n        SysFiles sysFiles = this.getById(id);\\n        if(ObjectUtil.isEmpty(sysFiles)) {\\n            throw new CommonException(\\\"文件管理表不存在，id值为：{}\\\", id);\\n        }\\n        return sysFiles;\\n    }\\n\\n    @Override\\n    public void downloadImportTemplate(HttpServletResponse response) throws IOException {\\n       File tempFile = null;\\n       try {\\n         List<SysFilesEditParam> dataList = CollectionUtil.newArrayList();\\n         String fileName = \\\"文件管理表导入模板_\\\" + DateUtil.format(DateTime.now(), DatePattern.PURE_DATETIME_PATTERN) + \\\".xlsx\\\";\\n         tempFile = FileUtil.file(FileUtil.getTmpDir() + FileUtil.FILE_SEPARATOR + fileName);\\n         EasyExcel.write(tempFile.getPath(), SysFilesEditParam.class).sheet(\\\"文件管理表\\\").doWrite(dataList);\\n         CommonDownloadUtil.download(tempFile, response);\\n       } catch (Exception e) {\\n         log.error(\\\">>> 文件管理表导入模板下载失败：\\\", e);\\n         CommonResponseUtil.renderError(response, \\\"文件管理表导入模板下载失败\\\");\\n       } finally {\\n         FileUtil.del(tempFile);\\n       }\\n    }\\n\\n    @Transactional(rollbackFor = Exception.class)\\n    @Override\\n    public JSONObject importData(MultipartFile file) {\\n        try {\\n            int successCount = 0;\\n            int errorCount = 0;\\n            JSONArray errorDetail = JSONUtil.createArray();\\n            // 创建临时文件\\n            File tempFile = FileUtil.writeBytes(file.getBytes(), FileUtil.file(FileUtil.getTmpDir() +\\n                    FileUtil.FILE_SEPARATOR + \\\"sysFilesImportTemplate.xlsx\\\"));\\n            // 读取excel\\n            List<SysFilesEditParam> sysFilesEditParamList =  EasyExcel.read(tempFile).head(SysFilesEditParam.class).sheet()\\n                    .headRowNumber(1).doReadSync();\\n            List<SysFiles> allDataList = this.list();\\n            for (int i = 0; i < sysFilesEditParamList.size(); i++) {\\n                JSONObject jsonObject = this.doImport(allDataList, sysFilesEditParamList.get(i), i);\\n                if(jsonObject.getBool(\\\"success\\\")) {\\n                    successCount += 1;\\n                } else {\\n                    errorCount += 1;\\n                    errorDetail.add(jsonObject);\\n                }\\n            }\\n            return JSONUtil.createObj()\\n                    .set(\\\"totalCount\\\", sysFilesEditParamList.size())\\n                    .set(\\\"successCount\\\", successCount)\\n                    .set(\\\"errorCount\\\", errorCount)\\n                    .set(\\\"errorDetail\\\", errorDetail);\\n        } catch (Exception e) {\\n            log.error(\\\">>> 文件管理表导入失败：\\\", e);\\n            throw new CommonException(\\\"文件管理表导入失败\\\");\\n        }\\n    }\\n\\n    public JSONObject doImport(List<SysFiles> allDataList, SysFilesEditParam sysFilesEditParam, int i) {\\n        String id = sysFilesEditParam.getId();\\n        if(ObjectUtil.hasEmpty(id)) {\\n            return JSONUtil.createObj().set(\\\"index\\\", i + 1).set(\\\"success\\\", false).set(\\\"msg\\\", \\\"必填字段存在空值\\\");\\n        } else {\\n            try {\\n                int index = CollStreamUtil.toList(allDataList, SysFiles::getId).indexOf(sysFilesEditParam.getId());\\n                SysFiles sysFiles;\\n                boolean isAdd = false;\\n                if(index == -1) {\\n                    isAdd = true;\\n                    sysFiles = new SysFiles();\\n                } else {\\n                    sysFiles = allDataList.get(index);\\n                }\\n                BeanUtil.copyProperties(sysFilesEditParam, sysFiles);\\n                if(isAdd) {\\n                    allDataList.add(sysFiles);\\n                } else {\\n                    allDataList.remove(index);\\n                    allDataList.add(index, sysFiles);\\n                }\\n                this.saveOrUpdate(sysFiles);\\n                return JSONUtil.createObj().set(\\\"success\\\", true);\\n            } catch (Exception e) {\\n              log.error(\\\">>> 数据导入异常：\\\", e);\\n              return JSONUtil.createObj().set(\\\"success\\\", false).set(\\\"index\\\", i + 1).set(\\\"msg\\\", \\\"数据导入异常\\\");\\n            }\\n        }\\n    }\\n\\n    @Override\\n    public void exportData(List<SysFilesIdParam> sysFilesIdParamList, HttpServletResponse response) throws IOException {\\n       File tempFile = null;\\n       try {\\n         List<SysFilesEditParam> dataList;\\n         if(ObjectUtil.isNotEmpty(sysFilesIdParamList)) {\\n            List<String> idList = CollStreamUtil.toList(sysFilesIdParamList, SysFilesIdParam::getId);\\n            dataList = BeanUtil.copyToList(this.listByIds(idList), SysFilesEdi